package guvi;
import java.util.*;
public class Sumofn {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner sc=new Scanner(System.in);
int n=sc.nextInt();
int sum=0;
while(n>0) {
sum+=n;
n--;
	}
if(sum<9)
System.out.println(sum);
else
	System.out.println(sum);
sc.close();
	}

}
